import React, { useEffect } from 'react';
import './Header.css';
import gsap from 'gsap';

function Header() {
    useEffect(() => {
        const letters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
        let interval = null;

        document.querySelectorAll(".first-name, .last-name").forEach(element => {
            element.onmouseover = event => {
                let iteration = 0;
                clearInterval(interval);
                interval = setInterval(() => {
                    event.target.innerText = event.target.innerText
                        .split("")
                        .map((letter, index) => {
                            if (index < iteration) {
                                return event.target.dataset.value[index];
                            }
                            return letters[Math.floor(Math.random() * 26)];
                        })
                        .join("");
                    if (iteration >= event.target.dataset.value.length) {
                        clearInterval(interval);
                    }
                    iteration += 1 / 3;
                }, 30);
            };
        });
    }, []);

    return (
        <div className="header">
            <div className="header-left">
                <span className="first-name" data-value="garrettaudet">garrett</span>
                <span className="collapse-on-hover"> </span>
                <span className="last-name" data-value="audet">audet</span>
                <span className="logo-letter-hide delay-75">.</span>
                <span className="logo-letter-hide delay-100">com</span>
            </div>
            <div className="header-right">
                <div>About</div>
                <div>Experience</div>
                <a href="https://garrett-audet.medium.com/">Writing</a>
                <div>Contact</div>
            </div>
        </div>
    );
}

export default Header;
