import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import './header.css';

function Header({ setPosition }) {
    useEffect(() => {
        const letters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
        let interval = null;
        let isOriginal = true;

        const firstNameElement = document.querySelector(".first-name");

        firstNameElement.onmouseover = event => {
            let iteration = 0;
            const originalName = "garrettaudet.com";
            const fullName = "garrett audet";
            const targetText = isOriginal ? fullName : originalName;

            clearInterval(interval);
            interval = setInterval(() => {
                event.target.innerText = targetText
                    .split("")
                    .map((letter, index) => {
                        if (index < iteration) {
                            return targetText[index];
                        }
                        return letters[Math.floor(Math.random() * 26)];
                    })
                    .join("");

                if (iteration >= targetText.length) {
                    clearInterval(interval);
                    isOriginal = !isOriginal;
                }
                iteration += 1 / 3;
            }, 30);
        };

        return () => clearInterval(interval);
    }, []);

    return (
        <div className="header">
            <div className="header-left">
                <span className="first-name" data-value="garrettaudet.com">garrettaudet.com</span>
            </div>
            <div className="header-right">
                <Link to="/" onClick={() => setPosition('absolute')}>About</Link>
                <Link to="/experience" onClick={() => setPosition('relative')}>Experience</Link>
                <a href="https://garrett-audet.medium.com/">Writing</a>
                <Link to="/experience#contact" onClick={() => setPosition('relative')}>Contact</Link> {/* Use hash link for scrolling */}
            </div>
        </div>
    );
}

export default Header;
