import React, { useRef, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import './App.css';
import Content from './Content/Content';
import Cursor from './Cursor/Cursor';
import LandingPage from './LandingPage/landing-page';
import Header from './LandingPage/header';

function App() {
  const experienceRef = useRef(null);
  const contactRef = useRef(null);

  const scrollToSection = (sectionRef) => {
    sectionRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  const location = useLocation(); // Hook to get current route location

  useEffect(() => {
    // Reinitialize or trigger cursor behavior on route change
    // Assuming you have custom logic in your Cursor component
    const cursor = document.querySelector('.custom-cursor');
    const links = document.querySelectorAll('a');

    // Add hover listeners to all anchor tags
    links.forEach(link => {
      link.addEventListener('mouseenter', () => cursor.classList.add('expand'));
      link.addEventListener('mouseleave', () => cursor.classList.remove('expand'));
    });

    // Clean up to avoid memory leaks
    return () => {
      links.forEach(link => {
        link.removeEventListener('mouseenter', () => cursor.classList.add('expand'));
        link.removeEventListener('mouseleave', () => cursor.classList.remove('expand'));
      });
    };
  }, [location]); // Re-run effect on route change

  return (
    <Router>
      <div className="scrolling-container particles-js data-simplebar">
        <Header scrollToSection={scrollToSection} experienceRef={experienceRef} contactRef={contactRef} />
        <Routes>
          <Route
            path="/"
            element={<LandingPage scrollToSection={scrollToSection} experienceRef={experienceRef} contactRef={contactRef} />}
          />
          <Route path="/content" element={<Content />} />
        </Routes>
        <Cursor />
      </div>
    </Router>
  );
}

export default App;
