import React, { useEffect } from 'react';
import './Header.css';

function Header() {
    useEffect(() => {
        const letters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
        let interval = null;

        const firstNameElement = document.querySelector(".first-name");

        firstNameElement.onmouseover = event => {
            let iteration = 0;
            const fullName = "garrettaudet.com";
            clearInterval(interval);
            interval = setInterval(() => {
                event.target.innerText = fullName
                    .split("")
                    .map((letter, index) => {
                        if (index < iteration) {
                            return fullName[index];
                        }
                        return letters[Math.floor(Math.random() * 26)];
                    })
                    .join("");
                if (iteration >= fullName.length) {
                    clearInterval(interval);
                }
                iteration += 1 / 3;
            }, 30);
        };

        firstNameElement.onmouseleave = event => {
            let iteration = 0;
            const originalName = "garrett audet";
            clearInterval(interval);
            interval = setInterval(() => {
                event.target.innerText = originalName
                    .split("")
                    .map((letter, index) => {
                        if (index < iteration) {
                            return originalName[index];
                        }
                        return letters[Math.floor(Math.random() * 26)];
                    })
                    .join("");
                if (iteration >= originalName.length) {
                    clearInterval(interval);
                }
                iteration += 1 / 3;
            }, 30);
        };
    }, []);

    return (
        <div className="header">
            <div className="header-left">
                <span className="first-name" data-value="garrettaudet.com">garrett audet</span>
            </div>
            <div className="header-right">
                <div>About</div>
                <div>Experience</div>
                <a href="https://garrett-audet.medium.com/">Writing</a>
                <div>Contact</div>
            </div>
        </div>
    );
}

export default Header;
