import React, { useEffect } from 'react';
import './Header.css';
import gsap from 'gsap';
import SplitTextJS from 'split-text'; // Ensure this is the correct way to import based on how you include SplitTextJS

function Header() {
    useEffect(() => {
        const titles = gsap.utils.toArray('.transformName');
        const t1 = gsap.timeline();

        titles.forEach(title => {
            const splitTitle = new SplitTextJS(title, { type: 'chars' });
            t1
                .from(splitTitle.chars, {
                    opacity: 0,
                    y: 80,
                    rotateX: -90,
                    stagger: 0.02,
                    duration: 1
                })
                .to(splitTitle.chars, {
                    opacity: 0,
                    y: -80,
                    rotateX: 90,
                    stagger: 0.02,
                    duration: 1
                }, "+=1"); // Ensure there's a delay before starting the exit animation
        });
    }, []);

    return (
        <div className="header">
            <div className="header-left">
                <div className="text-wrapper">
                    <p className="transformName">garrett audet</p>
                    <p className="transformName">garrettaudet.com</p>
                </div>
            </div>
            <div className="header-right">
                <div>About</div>
                <div>Experience</div>
                <a href="https://garrett-audet.medium.com/">Writing</a>
                <div>Contact</div>
            </div>
        </div>
    );
}

export default Header;
