import React, { useEffect, useRef } from 'react';
import './Titlecard.css';

const TitleCard = () => {
  const textRef = useRef(null);
  const texts = [' business', ' technology', ' innovation'];
  let textIndex = 0;
  let charIndex = 0;
  let isDeleting = false;
  const typingSpeed = 200;
  const deletingSpeed = 100;
  const delayBetweenTexts = 2000;

  useEffect(() => {
    const type = () => {
      const currentText = texts[textIndex];
      if (isDeleting) {
        charIndex--;
        if (charIndex === 0) {
          isDeleting = false;
          textIndex = (textIndex + 1) % texts.length;
          setTimeout(type, 500);
          return;
        }
      } else {
        charIndex++;
        if (charIndex === currentText.length) {
          isDeleting = true;
          setTimeout(type, delayBetweenTexts);
          return;
        }
      }
      textRef.current.textContent = currentText.substring(0, charIndex);
      setTimeout(type, isDeleting ? deletingSpeed : typingSpeed);
    };
    type();
  }, []);

  return (
    <a href="about">
      <div className="header-container">
        <h2>
          <span className="inline-block">I'm a developer and business practitioner who believes</span>
          <span className="inline-block">
            that <span className="text-decoration"> bridging perspectives </span> unlocks 
            <span className="text-animation" ref={textRef}></span> opportunities
          </span>
        </h2>
      </div>
    </a>
  );
};

export default TitleCard;
