import React, { useState, useEffect } from 'react';
import { TypeAnimation } from 'react-type-animation';
import './TitleCard.css';

const TitleCard = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 2000); // 2000ms = 2 seconds delay

    return () => clearTimeout(timer);
  }, []);

  return (
    <a href="about">
      <div className={`header-container ${isVisible ? 'visible' : ''}`}>
        <h2> 
          <span className="inline-block appearIn">I'm a developer and business professional who believes that </span>
          <span className="inline-block appearIn"><span className="text-decoration"> bridging diverse perspectives </span> unlocks <span className="text-animation">
            <TypeAnimation
              sequence={[
                'economic potential.', 3000,
                'career success.', 3000,
                'individual growth.', 3000
              ]}
              wrapper="span"
              speed={10}
              style={{ fontSize: '1em', display: 'inline-block' }}
              repeat={Infinity}
            />
          </span></span>
        </h2>
      </div>
    </a>
  );
};

export default TitleCard;
